import React from "react";
import { useParams, useHistory } from "react-router-dom";

const AnimalSingle = () => {
    let { animal } = useParams();
    let history = useHistory();

    const Capitalize = (str) => {
        return str.chartAt(0).toUpperCase() + str.slice(1);

    }

    return (
        <div className="single-animal">
            <h1>This is the page for
                <span className="animal-span">  {animal}</span>
            </h1>
            <p>{this.Capitalize({ animal })}</p>
            <div className="animal-image">
                <img src=
                    {`https://source.unsplash.com/1600x900/?${animal}`} alt="image magic" />
            </div>
            <p>Check here if wikipedia has page</p>
            <div>
                <button onClick={() => history.goBack()}>
                    Back to animals
                </button>
            </div>
        </div>
    );
};

export default AnimalSingle;
